catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    // g_form.setVisible('conference', true);
    //g_form.setVisible('conference_details', true);
    //Type appropriate comment here, and begin script below
    var meeting_place = g_form.getValue("meeting_place");

    if (meeting_place == 'conference') {
        g_form.setDisplay('conference', true);
        g_form.setDisplay('conference_details', true);
        g_form.setDisplay('premise_location', false);
        g_form.setDisplay('remote_location', false);
    } else if (meeting_place == 'premises') {
        g_form.setDisplay('premise_location', true);
        g_form.setDisplay('conference', false);
        g_form.setDisplay('conference_details', false);
        g_form.setDisplay('remote_location', false);
    } else if (meeting_place == 'remote') {
        g_form.setDisplay('remote_location', true);
        g_form.setDisplay('conference', false);
        g_form.setDisplay('conference_details', false);
        g_form.setDisplay('premise_location', false);
    } else {
        g_form.setDisplay('remote_location', false);
        g_form.setDisplay('conference', false);
        g_form.setDisplay('conference_details', false);
        g_form.setDisplay('premise_location', false);
	}
}
