catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var request_type = g_form.getValue("u_request_type");

    if (request_type == 'legal') {
		g_form.clearOptions('u_what_needed');
		g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
		g_form.addOption('u_what_needed', 'legal2', 'Legal 2');		
		/*
        g_form.removeOption('u_what_needed', 'hr1');
        g_form.removeOption('u_what_needed', 'hr2');
        g_form.removeOption('u_what_needed', 'facilities1');
        g_form.removeOption('u_what_needed', 'facilities2');
        g_form.removeOption('u_what_needed', 'other');
		*/
    } else if (request_type == 'hr') {
		g_form.clearOptions('u_what_needed');
		g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
		g_form.addOption('u_what_needed', 'hr2', 'Human Resources 2');
    } else if (request_type == 'facilities') {
		g_form.clearOptions('u_what_needed');
		g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
		g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
    } else if (request_type == 'other') {
		g_form.clearOptions('u_what_needed');
		g_form.addOption('u_what_needed', 'other', 'Other');
    } else {
		g_form.clearOptions('u_what_needed');
    }
}
